using Microsoft.AspNetCore.Mvc;
using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using TesteVaga.Models; 

namespace TesteVaga.Controllers
{
    [Route("api/[controller]")]
    [ApiController]
    public class ProductsController : ControllerBase
    {
        
        private static readonly List<Product> Products = new List<Product>
        {
            new Product { Id = 1, Name = "Prego", Description = "Os pregos são pequenos objetos metálicos com uma extremidade afiada e uma cabeça geralmente", ImageUrl = "data:image/png;base64,", Price = 10.00m },
            new Product { Id = 2, Name = "Tramontina - Martelo de Borracha", Description = "Martelo Borracha 60mm 40684/060 - TRAMONTINA MASTER", ImageUrl = "data:image/jpeg;base64,", Price = 20.00m },
            new Product { Id = 2, Name = "Bosch - Martelo", Description = "A funcionalidade e qualidade das ferramentas Tramontina você encontra no Martelo de Unha Polido 20 mm com o Cabo em Madeira Envernizada.", ImageUrl = "data:image/jpeg;base64,", Price = 20.00m },
            new Product { Id = 2, Name = "Vap - Lavadora de Alta Pressão", Description = "Lavadora de Alta Pressão WAP Ágil 1800 Engate Rápido 1400W 1300 PSI\r\n\r\n", ImageUrl = "data:image/jpeg;base64,", Price = 200.00m },
            new Product { Id = 2, Name = "Makita - Furadeira Canulada Ortopédica Mandril ", Description = "Furadeira Canulada Ortopédica com Mandril Inóx.", ImageUrl = "data:image/jpeg;base64,", Price = 550.00m },
            new Product { Id = 2, Name = "Bosch - Nível A Laser De Linhas Verdes 15 M", Description = "Nível A Laser De Linhas Verdes 15 M Com Pontos Bosch\r\n", ImageUrl = "data:image/jpeg;base64,", Price = 880.00m },
           
        };

        
        private readonly string ImagesFolder = Path.Combine(Directory.GetCurrentDirectory(), "wwwroot", "imagens");

       
        [HttpGet]
        public IEnumerable<Product> GetProducts(string searchTerm = "")
        {
            if (string.IsNullOrWhiteSpace(searchTerm))
            {
                return Products;
            }
            return Products.Where(p => p.Name.Contains(searchTerm, StringComparison.OrdinalIgnoreCase) ||
                                       p.Description.Contains(searchTerm, StringComparison.OrdinalIgnoreCase));
        }

        
        [HttpGet("{id}")]
        public ActionResult<Product> GetProduct(int id)
        {
            var product = Products.FirstOrDefault(p => p.Id == id);
            if (product == null)
            {
                return NotFound();
            }
            return product;
        }
    }
}
